sql_queries = {'SLT_WF_PO_APPR_FULL_STEPS_V2':'''SELECT DISTINCT A.BUSINESS_UNIT, A.PO_ID AS PO_ID, B.PO_DT AS PO_DT, B.PO_STATUS AS PO_STATUS, D.OPRID AS OPRID, K.OPRDEFNDESC AS OPRDEFNDESC, from_unixtime(unix_timestamp(D.DTTM_CREATED, 'dd-MMM-yyyy hh:mm:ss'), 'MM/dd/yyyy HH:mm:ss') AS INITIATED_DATETIME, from_unixtime(unix_timestamp(D.EOAWDTTM_MODIFIED, 'dd-MMM-yyyy hh:mm:ss'), 'MM/dd/yyyy HH:mm:ss') AS APPROVAL_DATETIME, CAST((D.EOAWDTTM_MODIFIED - D.DTTM_CREATED) AS STRING) AS PENDING_DAYS, D.EOAWORIG_OPRID, D.EOAWSTEP_STATUS, D.EOAWSTEP_INSTANCE, A.EOAWTHREAD_ID, C.EOAWPATH_ID, date_format(to_date(G.APPROVAL_DT, 'dd-MMM-yyyy hh:mm:ss'), 'MM/dd/yyyy') AS REQ_APPR_DATE, G.BUSINESS_UNIT, G.REQ_ID, G.HOLD_STATUS, H.BUYER_ID, I.LINE_NBR, I.MB_HOLD_TYPE, I.DUE_DT AS DUE_DT, I.COMMENTS60                  FROM GQL_PO_AW A, GQL_PO_APPR_HDR_VW B, GQL_EOAW_STEPINST C, GQL_EOAW_USERINST D, GQL_PSOPRDEFN K, GQL_PO_LINE_DISTRIB E, GQL_REQ_LN_DISTRIB F, GQL_REQ_HDR G, (GQL_MB_REQ_HOLD_HDR H LEFT OUTER JOIN  GQL_MB_REQ_HOLD_LN I ON  H.BUSINESS_UNIT = I.BUSINESS_UNIT AND H.REQ_ID = I.REQ_ID )                                                                                                                          WHERE ( B.BUSINESS_UNIT = A.BUSINESS_UNIT                                                                                                                                                                      AND B.PO_ID = A.PO_ID                                                                                                                                                                                      AND A.EOAWTHREAD_ID = C.EOAWTHREAD_ID                                                                                                                                                                      AND A.EOAWPRCS_ID = C.EOAWPRCS_ID                                                                                                                                                                          AND C.EOAWDEFN_ID = A.EOAWDEFN_ID                                                                                                                                                                          AND C.EOAWSTEP_INSTANCE = D.EOAWSTEP_INSTANCE                                                                                                                                                              AND K.OPRID = D.OPRID                                                                                                                                                                                      AND E.BUSINESS_UNIT = A.BUSINESS_UNIT                                                                                                                                                                      AND E.PO_ID = A.PO_ID                                                                                                                                                                                      AND E.BUSINESS_UNIT = F.BUSINESS_UNIT                                                                                                                                                                      AND E.LINE_NBR = F.LINE_NBR                                                                                                                                                                                AND E.SCHED_NBR = F.SCHED_NBR                                                                                                                                                                              AND E.DISTRIB_LINE_NUM = F.DISTRIB_LINE_NUM                                                                                                                                                                AND F.REQ_ID = E.REQ_ID                                                                                                                                                                                    AND F.BUSINESS_UNIT = G.BUSINESS_UNIT                                                                                                                                                                      AND F.REQ_ID = G.REQ_ID                                                                                                                                                                                    AND G.BUSINESS_UNIT = H.BUSINESS_UNIT                                                                                                                                                                      AND G.REQ_ID = H.REQ_ID)                                                                                                                                                                                   ORDER BY 2, 8, 12''',
            'SLT_REQS_ON_HOLD_HISTORY':'''SELECT A.BUSINESS_UNIT AS BUSINESS_UNIT, A.REQ_ID AS REQ_ID, A.REQ_STATUS AS REQ_STATUS, A.HOLD_STATUS AS HOLD_STATUS, from_unixtime(unix_timestamp(A.APPROVAL_DT, 'dd-MMM-yyyy hh:mm:ss'), 'MM/dd/yyyy') AS APPROVAL_DATE, A.ORIGIN AS ORIGIN, A.OPRID_ENTERED_BY AS OPRID_ENTERED_BY, A.OPRID_MODIFIED_BY AS OPRID_MODIFIED_BY, from_unixtime(unix_timestamp(A.ENTERED_DT, 'dd-MMM-yyyy hh:mm:ss'), 'MM/dd/yyyy') AS REQ_ENTERED, from_unixtime(unix_timestamp(A.REQ_DT, 'dd-MMM-yyyy hh:mm:ss'), 'MM/dd/yyyy') AS REQ_DATE, D.LINE_NBR AS LINE_NBR, B.MB_HOLD_STATUS AS MB_HOLD_STATUS, B.BUYER_ID AS BUYER_ID, B.LAST_UPDATED_BY AS LAST_UPDATED_BY, C.LINE_NBR AS LINE_NBR, from_unixtime(unix_timestamp(C.DATETIME_STAMP, 'dd-MMM-yyyy hh:mm:ss'), 'MM/dd/yyyy HH:mm:ss') AS HOLD_LINE_DATETIME, from_unixtime(unix_timestamp(C.DUE_DT, 'dd-MMM-yyyy hh:mm:ss'), 'MM/dd/yyyy') AS DUE_DATE, C.MB_LINE_STATUS AS MB_LINE_STATUS, C.MB_HOLD_TYPE AS MB_HOLD_TYPE, C.COMMENTS60 AS COMMENTS60, F.BUSINESS_UNIT, F.PO_ID AS PO_ID, F.LINE_NBR                                                                                                                                                                           FROM GQL_REQ_HDR A, GQL_MB_REQ_HOLD_HDR B, GQL_MB_REQ_HOLD_LN C, GQL_REQ_LINE D, (GQL_REQ_LN_DISTRIB E LEFT OUTER JOIN  GQL_PO_LINE_DISTRIB F ON  E.BUSINESS_UNIT = F.BUSINESS_UNIT AND E.REQ_ID = F.REQ_ID AND E.LINE_NBR = F.LINE_NBR AND E.SCHED_NBR = F.SCHED_NBR AND E.DISTRIB_LINE_NUM = F.DISTRIB_LINE_NUM )                                                                                                  WHERE ( A.BUSINESS_UNIT = B.BUSINESS_UNIT                                                                                                                                                                      AND A.REQ_ID = B.REQ_ID                                                                                                                                                                                    AND B.BUSINESS_UNIT = C.BUSINESS_UNIT                                                                                                                                                                      AND B.REQ_ID = C.REQ_ID                                                                                                                                                                                    AND A.BUSINESS_UNIT = D.BUSINESS_UNIT                                                                                                                                                                      AND A.REQ_ID = D.REQ_ID                                                                                                                                                                                    AND D.BUSINESS_UNIT = E.BUSINESS_UNIT                                                                                                                                                                      AND D.REQ_ID = E.REQ_ID                                                                                                                                                                                    AND D.LINE_NBR = E.LINE_NBR                                                                                                                                                                                AND C.MB_LINE_STATUS IN ('C','N','X'))                                                                                                                                                                     ORDER BY 4 DESC, 5 DESC, 2, 15'''}
